mapper.select:
select file_seq ,file_grp_id ,file_org_name ,file_new_name ,file_path ,file_size ,file_ext from tb_attach_file where use_yn='Y'
and file_grp_id =#{file_grp_id}
and file_grp_id in
#{item}

mapper.update:

  insert into tb_attach_file
 (  file_seq
	,file_grp_id  
	,file_org_name 
	,file_new_name 
	,file_path 
	,file_size 
	,file_ext
	,use_yn
	,create_date
	,update_date
	,create_user_id
	,update_user_id

)
values(
	 nextval(tb_attach_file_seq)
	,#{file_grp_id}  
	,#{file_org_name} 
	,#{file_new_name }
	,#{file_path }
	,#{file_size }
	,#{file_ext}
	,'Y'
	,now()
	,now()
	,#{create_user_id}
	,#{update_user_id}
	)

UPDATE tb_attach_file 
SET    file_grp_id = #{newGrp} 
WHERE  file_grp_id = #{oldGrp}
